package com.zaq.core.common;

import java.nio.charset.Charset;

import org.apache.mina.core.buffer.IoBuffer;
import org.apache.mina.core.session.IoSession;
import org.apache.mina.filter.codec.ProtocolEncoderAdapter;
import org.apache.mina.filter.codec.ProtocolEncoderOutput;

import com.zaq.core.util.AppUtil;

public class MyProtocalEncoder extends ProtocolEncoderAdapter {
	private final Charset charset;

	public MyProtocalEncoder(Charset charset) {
		this.charset = charset;
	}

	// 在此处实现包的编码工作，并把它写入输出流中
	public void encode(IoSession session, Object message, ProtocolEncoderOutput out) throws Exception {
		int size=0;
		boolean isTag=false;//回执标志
		if(message instanceof String){
			size=((String) message).getBytes(charset.name()).length;
		}else{
			size=8;//回执 long 8个字节
			isTag=true;
		}
		IoBuffer buf = IoBuffer.allocate(size);
		buf.setAutoExpand(true);
		
		buf.putInt(size+Integer.valueOf(AppUtil.getPropertity("Pack.packHeadLength")));
		if(!isTag){
			buf.put(charset.encode(((String) message)));
		}else{
			buf.putLong((Long)message);
		}
		buf.flip();
		out.write(buf);
	}
	
	public static void main(String[] args) {
		String str = "";
System.out.println(str.length());
	}
}
